import React, { useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Bird from './Bird';
import Casino from './Casino';
import { position, timerID } from './redux/actions';

export default function Gamezone() {
    const dispatch = useDispatch();
    const state = useSelector(state => state.position.position);
    const timerIdState = useSelector(state => state.timerID.id);

    const background = useRef(null)
    const [gameIsStart, setGameStart] = useState(false);

    const handleClick = () => {
        if (!gameIsStart) {
            dispatch(timerID(setInterval(() => dispatch(position(null, '-1px')), 7)));
            setGameStart(true);
            background.current.style.transition = '2s linear';
            background.current.style.backgroundPositionX = '100%';
        }
        dispatch(position('60px'));
    }
    const handleStop = (e) => {
        e.stopPropagation();
        clearInterval(timerIdState);
        setGameStart(false);
        dispatch(position('stop'))
    }
    const onTransition = (e) => {
        e.target.style.transition = '0s';
        e.target.style.backgroundPositionX = '0';
    }
    return (
        <div className='game-zone' onClick={handleClick} ref={background} onTransitionEnd={onTransition}>
            <input type='button' value='STOP' onClick={handleStop} />
            <img src="./"></img>
            <Bird />
            <Casino />
        </div>
    )
}
