import React, { useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Bird from './Bird';
import Casino from './Casino';
import { position, timerID } from './redux/actions';

export default function Gamezone() {
    const dispatch = useDispatch();
    const state = useSelector(state => state.position.position);
    const timerIdState = useSelector(state => state.timerID.id);

    const background = useRef(null)
    const casinoBottom = useRef(null)

    const [gameIsStart, setGameStart] = useState(false);

    const handleClick = () => {
        if (!gameIsStart) {
            dispatch(timerID(setInterval(() => dispatch(position(null, '-1px')), 5)));
            setGameStart(true);
            background.current.style.animationName = 'move';
            casinoBottom.current.style.animationName = 'moveCasino';
        }
        dispatch(position('70px'));
    }
    const handleRestart = (e) => {
        e.stopPropagation();
        background.current.style.animationName = '';
        casinoBottom.current.style.animationName = '';
        clearInterval(timerIdState);
        setGameStart(false);
        dispatch(position('stop'))
    }
    const onTransition = (e) => {
        console.log('transition end')
    }
    return (
        <div className='game-zone' onClick={handleClick} ref={background}>
            <input type='button' value='RESTART' onClick={handleRestart} />
            <Bird />
            <Casino reff={casinoBottom} />
        </div>
    )
}
