import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import Bird from './Bird';
import { position } from './redux/actions';

export default function Gamezone(){
    const dispatch = useDispatch();
    const [gameIsStart, setGameStart] = useState();
    
    const handleClick = () => {
        dispatch(position('40px'));
        setInterval( () => dispatch(position(null, '-1px')), 20)
    }   

    return(
        <div className='game-zone' onClick={handleClick}>
            <Bird />
        </div>
    )
}
