const initialState = {
    position: ''
}

export default function positionReducer(state = initialState, action){
    switch(action.type){
        case 'POSITION':
            return {
                position: parseInt(state.position.replace(/[^\d]/g, '')) + parseInt(action.data.replace(/[^\d]/g, '')) + 'px'
            }
        default:
            return state;
    }
}
