import { useSelector } from 'react-redux';

export default function Bird(){
    let positionState = useSelector(state => state.position.position);
    let characterState = useSelector(state => state);
    // console.log(characterState)
    let birdRef = useRef(null);
    useEffect( () => {
        console.log(characterState)
    }, [])
    useEffect( () => {birdRef.current.style.bottom = positionState},
     [positionState])
    return(
        <div className='bird' ref={birdRef}>
        </div>
    )
}
