import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import Bird from './Bird';
import { position } from './redux/actions';

export default function Gamezone(){
    const dispatch = useDispatch();
    const [gameIsStart, setGameStart] = useState(false);
    
    const handleClick = () => {
        if(!gameIsStart){
            setInterval( () => dispatch(position(null, '-1px')), 10);
            setGameStart(true)
        }
        dispatch(position('40px'));
    }   

    return(
        <div className='game-zone' onClick={handleClick}>
            <Bird />
        </div>
    )
}
