import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Bird from './Bird';
import { position, timerID } from './redux/actions';
import StopBtn from './StopBtn';

export default function Gamezone(){
    const dispatch = useDispatch();
    const state = useSelector( state => state.position.position);
    const timerIdState = useSelector( state => state.timerID);
    let timerID;
    const [gameIsStart, setGameStart] = useState(false);
    
    const handleClick = () => {
        if(!gameIsStart){
            timerID = setInterval( () => dispatch(position(null, '-1px')), 7);
            setGameStart(true)
        }
        dispatch(position('40px'));
    }   
    const handleStop = () => {
        e.preventDefault();
        console.log('iuu')
        clearInterval(timerID)
    }
    return(
        <div className='game-zone' onClick={handleClick}>
            <StopBtn onContextMenu = {handleStop}/>
            <Bird />
        </div>
    )
}
